  },
  // List all tags
  AllTagsSimple (sep) {
    if (!sep) sep = ', ';
    let allTags = tw.macros.std.allTags();
    return allTags.join(sep);
  },
  allTags () {
    let tags = [];
    tw.tiddlers.all.forEach(t => {
      t.tags.filter(t => !!t).forEach(tag => {
        tags.push(tag);
      });
    });
    return [...new Set(tags)];
  },
