  };
  tw.macros.std.TrashCanContents = () => {
    const list = tw.tiddlers.trashed.map(t => (`<li><a href="#msg:tiddlers.trashed.preview:${t.title}">${t.title}</a> <a title="Restore Tiddler" href="#msg:tiddler.trashed.restore:${t.title}">üöÆ</a></li>`)).join('\n');
    return `<ul id="trashContents">${list}</ul>`;
  };
  tw.run.trashPreview = (title) => {
    let tiddler = tw.tiddlers.trashed.find(t => t.title === title);
    tw.run.previewTiddler(tiddler, tw.templates.TiddlerTrashed);
  };
  tw.run.removeTiddlerFromTrash = (title) => {
    if (!confirm('Are you sure you want to permanently delete your trashed tiddler?')) return;
    let tiddlerIndex = tw.tiddlers.trashed.findIndex(t => t.title === title);
    tw.tiddlers.trashed.splice(tiddlerIndex, 1);
    tw.run.trashCanRefresh();
    tw.dom.preview.close();
    tw.run.save();
  };
  tw.run.trashCanRefresh = () => {
    if (tw.dom.$('trashContents')) tw.dom.$('trashContents').outerHTML = tw.lib.markdown(tw.macros.std.TrashCanContents());
    if (tw.dom.$('trashCanIcon')) tw.dom.$('trashCanIcon').innerHTML = tw.macros.std.TrashCanIcon();
    if (tw.dom.$('trashStatus')) tw.dom.$('trashStatus').outerHTML = tw.lib.markdown(tw.macros.std.TrashCanStatus());

    tw.run.rerenderTiddler('$TrashedTiddlers');
  };
  tw.run.restoreTiddlerFromTrash = (title) => {
    if (!confirm('Are you sure you want to restore your trashed tiddler? This may overwrite an existing tiddler!')) return;
    let tiddler = tw.tiddlers.trashed.find(t => t.title === title);
    tw.run.addTiddler(tiddler);
    tw.run.reload();
    // So styles, search list etc update
    let tiddlerIndex = tw.tiddlers.trashed.findIndex(t => t.title === title);
    tw.tiddlers.trashed.splice(tiddlerIndex, 1);
    tw.run.trashCanRefresh();
    tw.run.save();
  };
  tw.events.subscribe('tiddlers.trashed.empty', tw.run.emptyTrash, moduleName);
  tw.events.subscribe('tiddler.deleted', tw.run.trashCanRefresh, moduleName);
  tw.events.subscribe('tiddler.trashed.restore', tw.run.restoreTiddlerFromTrash, moduleName);
  tw.events.subscribe('tiddler.trashed.destroy', tw.run.removeTiddlerFromTrash, moduleName);
  tw.events.subscribe('tiddlers.trashed.preview', tw.run.trashPreview, moduleName);
})();
