tw.macros.std.TrashCanIcon = () => {
  let count = tw.tiddlers.trashed.length;
  return tw.ui.button(`{{$IconDelete}} (${count})`, 'tiddler.show', '$TrashManager', 'trashCanIcon');
  // return `<a id="trashCanIcon" href="#$TrashManager">üóëÔ∏è (${count})</a>`;
};
tw.macros.std.TrashEmptyButton = () => {
  return tw.ui.button('Empty Trash', 'tiddlers.trashed.empty', null, 'trashButton');
};
tw.macros.std.TrashCanStatus = () => {
  return `<span id="trashStatus">${tw.tiddlers.trashed.length} tiddlers in the [trash can](#$TrashedTiddlers)!`;
};
tw.run.emptyTrash = () => {
  if (!confirm('Are you sure you want to permanently deleted your trashed tiddlers?')) return;
  tw.tiddlers.trashed = [];
  tw.run.trashCanRefresh();
  tw.run.save();
};
tw.macros.std.TrashCanContents = () => {
  const list = tw.tiddlers.trashed.map(t => (`<li><a href="#msg:tiddlers.trashed.preview:${t.title}">${t.title}</a> <a title="Restore Tiddler" href="#msg:tiddler.restore:${t.title}">üöÆ</a></li>`)).join('\n');
  return `<ul id="trashContents">${list}</ul>`;
};
tw.run.trashPreview = (title) => {
  let tiddler = tw.tiddlers.trashed.find(t => t.title === title);
  tw.run.previewTiddler(tiddler, false);
  tw.dom.preview.querySelector('button.delete').addEventListener('click', tw.run.removeTiddlerFromTrash);
};
tw.run.removeTiddlerFromTrash = (title) => {
  if (!confirm('Are you sure you want to permanently delete your trashed tiddler?')) return;
  let tiddlerIndex = tw.tiddlers.trashed.findIndex(t => t.title === title);
  tw.tiddlers.trashed.splice(tiddlerIndex, 1);
  tw.run.trashCanRefresh();
  tw.dom.preview.close();
  tw.run.save();
};
tw.run.trashCanRefresh = () => {
  if (tw.dom.$('trashContents')) tw.dom.$('trashContents').outerHTML = tw.lib.markdown(tw.macros.std.TrashCanContents());
  if (tw.dom.$('trashCanIcon')) tw.dom.$('trashCanIcon').innerHTML = tw.macros.std.TrashCanIcon();
  if (tw.dom.$('trashStatus')) tw.dom.$('trashStatus').outerHTML = tw.lib.markdown(tw.macros.std.TrashCanStatus());

  tw.run.rerenderTiddler('$TrashedTiddlers');
};
tw.run.restoreTiddlerFromTrash = (title) => {
  if (!confirm('Are you sure you want to restore your trashed tiddler? This may overwrite an existing tiddler!')) return;
  let tiddler = tw.tiddlers.trashed.find(t => t.title === title);
  tw.run.addTiddler(tiddler);
  tw.run.reload();
  // So styles, search list etc update
  let tiddlerIndex = tw.tiddlers.trashed.findIndex(t => t.title === title);
  tw.tiddlers.trashed.splice(tiddlerIndex, 1);
  tw.run.trashCanRefresh();
  tw.run.save();
};
tw.events.subscribe('tiddlers.trashed.empty', tw.run.emptyTrash);
tw.events.subscribe('tiddler.deleted', tw.run.trashCanRefresh);
tw.events.subscribe('tiddler.restore', tw.run.restoreTiddlerFromTrash);
tw.events.subscribe('tiddlers.trashed.preview', tw.run.trashPreview);
