tiddlers.text:
/**
 * ## Description
 * Provide <<favorites.toggle>> widget
 *   Toggles tag 'Favorite' on current tiddler
 *   for use on $TiddlerDisplay template
 */
/**
 * ## Data
 * ```json
 * {
 *   "version": 1.0.0
 * }
 * ```
 */

tw.macros.favorites = {
  toggle() {
    return tw.ui.button('â­', 'favorites.toggle', '{{ctx.currentTiddler}}', 'favoriteAdd');
  },
};
tw.events.subscribe('favorites.toggle', (t) => {
  if (t.readOnly) return tw.ui.notify('Tiddler is readonly!', 'E');
  if (t.doNotSave) tw.ui.notify('Tiddler will not be saved!', 'W');
  tw.call('tiddlerToggleTag', t.title, 'Favorite');
  tw.events.send('save.silent');
});
