/**
 * ## Description
 * Provide <<favorites.toggle>> widget
 *   Toggles tag 'Favorite' on current tiddler
 *   for use on $TiddlerDisplay template
 */
/**
 * ## Data
 * ```json
 * {
 *   "version": 1.0.0
 * }
 * ```
 */
(function(){
  tw.macros.favorites = {
    toggle() {
      // TODO: How to find the current tiddler/element?
      // let el = tw.core.dom.nearestAttribute
      return tw.ui.button('{{$IconFavorite}}', 'favorites.toggle', '$currentTiddler', 'favoriteAdd');
    },
  };
  tw.events.subscribe('favorites.toggle', favoriteToggle);
  function favoriteToggle(title) {
    const t = tw.call('getTiddler', title);
    if (!t) throw new Error(`Unknown tiddler '${title}'!`);
    // if (t.tags.includes('$NoEdit')) return tw.ui.notify('Tiddler is readonly!', 'E');
    if (t.doNotSave) tw.ui.notify('Tiddler will not be saved!', 'W');
    tw.call('tiddlerToggleTag', t.title, 'Favorite');
    let btn = tw.call('getTiddlerElement', title)?.querySelector('button[title=favorite]');
    // dp(t.tags.includes('Favorite'), btn);
    if (btn) {
      if (!t.tags.includes('Favorite'))
        btn.className += ' yellow';
    }
    // dp(btn.className);
    tw.events.send('save.silent');
  }
  // Add {{=favoriteClass}} to your $TiddlerDisplay
  tw.extend.tiddlerDetails.favoriteClass = function(t) {
    return t.tags.includes('Favorite') ? 'favorite' : '';
  };
})();
// TODO: How to add button:yellow svg {fill: yellow;} to theme?

/**
 * ## Description
 * Backup your data to [JSONSilo.com](https://jsonsilo.com)
 */
/**
 * ## Data
 * ```json
 * {
 *   "version": 1.0.0
 * }
 * ```
 */
// ## Code
// ```javascript
tw.macros.backup = {
  restoreButton() {
    return tw.ui.button('{{$IconRestore}}', 'backup.restore', null, 'restore', 'title="Restore Backup Data"');
  },
  backupButton() {
    return tw.ui.button('{{$IconBackup}}', 'backup.save', null, 'backup', 'title="Backup Data"');
  },
  async restore() {
    alert('JSONSilo');
    let settings = tw.call('getJSONObject', '$GeneralSettings');
    if (!settings || !settings.backup?.JSONSilo?.accessKey || !settings.backup?.JSONSilo?.siloId) return tw.ui.notify('No JSONSilo accessKey/siloId found in $GeneralSettings!', 'W');
    let res = await fetch('https://api.jsonsilo.com/' + settings.backup.JSONSilo.siloId, {
      headers: {
        'X-SILO-KEY': settings.backup.JSONSilo.accessKey,
        'Content-Type': 'application/json',
      },
    });
    if (!res.ok) return tw.ui.notify(`Restore failed '${res.status}' (see log)`, 'E');
    let result = await res.json();
    Object.assign(tw.tiddlers, result.record);
    tw.run.save();
    if (confirm('Restore complete. Would you like to reload?')) tw.events.send('reboot.hard');
    tw.ui.notify('Restore complete!', 'S');
  },
  async save() {
    alert('JSONSilo');
    let settings = tw.call('getJSONObject', '$GeneralSettings');
    if (!settings || !settings.backup?.JSONSilo?.accessKey || !settings.backup?.JSONSilo?.siloId) return tw.ui.notify('No JSONSilo accessKey/siloId found in $GeneralSettings!', 'W');
    let body = JSON.stringify({
      all: tw.tiddlers.all,
      visible: tw.tiddlers.visible,
      trashed: tw.tiddlers.trashed,
    });
    let res = await fetch('https://api.jsonsilo.com/' + settings.backup.JSONSilo.siloId, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'X-SILO-KEY': settings.backup.JSONSilo.accessKey,
      },
      body,
    });
    if (!res.status === 403) return tw.ui.notify(`Backup failed '${res.status}': Unauthorized (see log)!`, 'E');
    if (!res.ok) return tw.ui.notify(`Backup failed '${res.status}' (see log)`, 'E');
    tw.ui.notify(`Backup complete! (${body.length / 1000} KB)`, 'S');
  },
};
tw.events.override('backup.save', tw.macros.backup.save);
tw.events.override('backup.restore', tw.macros.backup.restore);

/**
 * ## Description
 * Provide <<favorites.toggle>> widget
 *   Toggles tag 'Favorite' on current tiddler
 *   for use on $TiddlerDisplay template
 */
/**
 * ## Data
 * ```json
 * {
 *   "version": 1.0.0
 * }
 * ```
 */
(function(){
  let tiddlersToManage = [];
  tw.tmp.mass = {
    show(e, frm) {
      let o = {
        id: 'mass-search-list',
        all: true,
        title: frm.elements[0].value,
        tag: frm.elements[1].value,
        pck: frm.elements[2].value,
      };
      e.preventDefault();
      tiddlersToManage = tw.events.send('search.advanced', o)[0];
    },
    delete(e, frm) {
      e.preventDefault();
      this.show(e, frm); // tiddlersToManage = tw.events.send('search.advanced', o)[0];
      if (!tiddlersToManage.length) return alert('No tiddlers selected!');
      setTimeout(() => {
        if (!confirm(`Sure you want to delete these ${tiddlersToManage.length} tiddlers?`)) return;
        tiddlersToManage.forEach(t => (tw.run.deleteTiddler(t.title, true)));
      }, 300);
    },
  };
  tw.macros.mass = {
    delete() {
      return `<form>
      <input name=title placeholder=title>
      <input name=tag placeholder=tag>
      <input name=package placeholder=package>
      ${tw.ui.button('Show', null, null, 'btnMassShow', 'onclick="tw.tmp.mass.show(event, this.form)"')}
      ${tw.ui.button('Delete', null, null, 'btnMassDelete', 'onclick="tw.tmp.mass.delete(event, this.form)"')}
      </form><div id="mass-search-list">No tiddlers to show</div>`;
    },
  };
  tw.events.subscribe('tiddlers.delete.mass', massDelete);
  function massDelete({title, tag, pck}) {
    let list = tw.call('tiddlerSearch')({title, tag, pck})
      .map(t => t.title);
    if (!confirm(`Sure you want to delet these ${list.length} tiddlers?`)) return;
    list.forEach(title => (tw.run.deleteTiddler(title, true)));
  }
})();
